mapper.select:
SELECT * FROM (
	SELECT rownum rnum, R.* FROM (
	      SELECT * FROM users
		) R
	) RECORD
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM (
	SELECT rownum rnum, R.* FROM (
		SELECT * FROM users 
		
			
				
					AND user_nick LIKE '%' || #{keyword } || '%'
				
				
					AND user_name LIKE '%' || #{keyword } || '%'
				
			
		
		ORDER BY user_no 
		) R
	) RECORD
WHERE rnum BETWEEN #{startNo } AND #{endNo }

SELECT * FROM (
	SELECT rownum rnum, R.* FROM (
	      SELECT * FROM payment
		) R
	) RECORD
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM (
	SELECT rownum rnum, R.* FROM (
		SELECT * FROM payment 
		
			
					
						AND order_no LIKE '%' || #{keyword } || '%'
					
					
						AND p_name LIKE '%' || #{keyword } || '%'
					
			
		
		ORDER BY order_no 
		) R
	) RECORD
WHERE rnum BETWEEN #{startNo } AND #{endNo }
