mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			bf_no, bf_title, user_no,
			bf_hit, bf_thumbs, bf_inst_date
		FROM beforeafter
		ORDER BY bf_no DESC
	) B
) Beforeafter
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.insert:
INSERT INTO beforeafter ( 
	bf_no, bf_inst_date, bf_title, height, gender
	   , weight, bf_exercise, g_weight, s_date
	   , e_date, sele_exercise, before_con
	   , c_weight, g_result, after_con
	   , bf_upt_date, bf_delete_date, bf_hit
	   , bf_thumbs, user_no, category_no )

VALUES ( beforeafter_seq.nextval, to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), #{bfTitle}, #{height}, #{gender}
		   , #{weight}, #{bfExercise}, #{gWeight}, #{sDate}
		   , #{eDate}, #{seleExercise}, #{beforeCon}
		   , #{cWeight}, #{gResult}, #{afterCon}
		   ,  to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), 0
		   , 0, #{userNo}, 1 )

mapper.update:
UPDATE beforeafter
SET bf_title = #{bfTitle}
	, height = #{height}
	, gender = #{gender}
	, weight = #{weight}
	, bf_exercise = #{bfExercise}
	, g_weight = #{g_weight}
	, s_date = #{sDate}
	, e_date = #{eDate}
	, sele_exercise = #{seleExercise}
	, before_con = #{beforeCon}
	, c_weight = #{cWeight}
	, g_result = #{gResult}
	, after_con = #{afterCon}
WHERE bf_no = #{bfNo}
