mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			bf_no, bf_title, user_no,
			bf_hit, bf_thumbs, bf_inst_date
		FROM beforeafter
		ORDER BY bf_no DESC
	) B
) Beforeafter
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE beforeafter
SET bf_title = #{bfTitle}
	, height = #{height}
	, gender = #{gender}
	, weight = #{weight}
	, bf_exercise = #{bfExercise}
	, g_weight = #{gWeight}
	, s_date = #{sDate}
	, e_date = #{eDate}
	, sele_exercise = #{seleExercise}
	, before_con = #{beforeCon}
	, c_weight = #{cWeight}
	, g_result = #{gResult}
	, after_con = #{afterCon}
WHERE bf_no = #{bfNo}

mapper.select:



        SELECT bf_no, bf_title, user_no,
					bf_hit, bf_thumbs, bf_inst_date  FROM(

                SELECT 
                rownum  as rn, bf_no, bf_title, user_no,
								bf_hit, bf_thumbs, bf_inst_date

                FROM beforeafter WHERE rownum <= #{pageNum} * #{amount} 







                )

        WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE users 
	SET point = point + #{point}
WHERE user_no = #{userno}
