mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			bf_no, category_no, bf_title, user_no,
			bf_hit, bf_thumbs, bf_inst_date
		FROM beforeafter
		ORDER BY bf_no DESC
	) B
) Beforeafter
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.insert:
INSERT INTO beforeafter ( 
	bf_no, bf_inst_date, bf_title, height, gender
	   , weight, bf_exercise, g_weight, s_date
	   , e_date, sele_exercise, before_con
	   , c_weight, g_result, after_con
	   , bf_upt_date, bf_delete_date, bf_hit
	   , bf_thumbs, user_no, category_no )

VALUES ( beforeafter_seq.nextval, to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), #{bf_title}, #{height}, #{gender}
		   , #{weight}, #{bf_exercise}, #{g_weight}, #{s_date}
		   , #{e_date}, #{sele_exercise}, #{before_con}
		   , #{c_weight}, #{g_result}, #{after_con}
		   ,  to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), to_date('1991-11-05 00:00:00', 'YYYY-MM-DD HH24:MI:SS'), 0
		   , 0, #{user_no}, 1 )
