mapper.select:
SELECT * FROM (
	SELECT rownum rnum, B.* FROM (
		SELECT
			diet_no, d_title, user_no,
			d_hit, d_thumbs, d_inst_date
		FROM dietboard
		ORDER BY diet_no DESC
	) B
) DietBoard
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE dietboard
SET d_title = #{dTitle}
	, prod_classification = #{prodClassification}
	, d_content = #{dContent}
WHERE diet_no = #{dietNo}

mapper.select:



        SELECT diet_no, d_title, user_no,
					d_hit, d_thumbs, d_inst_date  FROM(

                SELECT 
                rownum  as rn, diet_no, d_title, user_no,
								d_hit, d_thumbs, d_inst_date

                FROM dietboard WHERE rownum <= #{pageNum} * #{amount} 







                )

        WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE users SET point =
	point + 50
WHERE user_no = #{userNo}
