mapper.select:
SELECT * FROM (
	SELECT rownum rnum, V.* FROM (
		SELECT
			verify_no, v_title, user_no,
			v_hit, v_thumbs, v_inst_date
		FROM verifyboard
		ORDER BY verify_no DESC
	) V
) VerifyBoard
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE verifyboard
SET v_title = #{vTitle}
	, exercise = #{exercise}
	, time = #{time}
	, kcal = #{kcal}
	, verify_content = #{verifyContent}
WHERE verify_no = #{verifyNo}

mapper.select:



        SELECT verify_no, v_title, user_no,
					v_hit, v_thumbs, v_inst_date  FROM(

                SELECT 
                rownum  as rn, verify_no, v_title, user_no,
								v_hit, v_thumbs, v_inst_date

                FROM verifyboard WHERE rownum <= #{pageNum} * #{amount} 







                )

        WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE users SET point =
	point + 50
WHERE user_no = #{userNo}
