mapper.select:
		SELECT * FROM (
 			SELECT rownum rnum, N.* FROM(
 				SELECT
 					notice_no, notice_ttl, notice_date, 
 					notice_hit, notice_content, notice_file
				FROM notice
				ORDER BY notice_no DESC
 			) N
 		) NOTICE
 		WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE notice
SET notice_ttl = #{noticeTtl} , notice_content = #{noticeContent}
WHERE notice_no = #{noticeNo}

mapper.select:
    
 
        
        SELECT notice_no, notice_ttl, notice_date, notice_hit, 
	       notice_file  FROM(
        
                SELECT 
                rownum  as rn, notice_no, notice_ttl, notice_date,
								notice_hit, notice_file
                  
                FROM notice WHERE rownum <= #{pageNum} * #{amount} 
                
                
                	 
                
    
    
                    
                )
                    
        WHERE rn > (#{pageNum} -1) * #{amount}
